var fs = require('fs') 
var body = '';
var readFile = function(req, res, callback){
  fs.readFile(req.files.file.path, 'utf-8', function(err, data){
    if(err || !data.length) {
      res.statusCode = 500;
      res.end('You do not upload a valid file');
    }
    callback(data);
  })
}

exports.readFile = readFile
